      expect(described_class.client).to be_an_instance_of(Smartling::Api)
    end
  end

  describe '#token' do
    let(:sample_jwt) do
      'ey65gy654t34tswfssfdsdf3..X_cXsqYXrxJzoBpRr3W0duiwPv72QHWtQ02Rhs_ZH9-nGmBb7jZ2MtwX-QOMJanIjFGeCwfsf3ozEemWY3HvpwFqv55HOFt2uVAFj3mLiADtSbKKiV-ixh5sY1pAcsjgNeQ-feMXjwpOIFgqQOWDhwc_yvDqAk9wKdMECMNcYa8'
    end

    it 'creates a JSON Web Token for Smartling' do
      expect(described_class).to receive(:token).and_return(sample_jwt)
      expect(described_class.token).to eql(sample_jwt)
    end
  end
end
